import React from 'react'
import { useState } from 'react'
import { useContext } from 'react'
import Cookies from 'universal-cookie'
import { Context } from '../Context'
import api, { API_URL } from '../http'





export default function RegistrationConfirm() {
	const { clearLoginForm, password, setPasswordEqual, userData, store, setAuth } = useContext(Context)
	const [confirmCode, setConfirmCode] = useState('')
	const [error, setError] = useState('')

	function submitHandler() {
		api.get(API_URL + '/user/confirmEmail', { id: store.user.id, code: confirmCode })
			.then(response => {
				setAuth(true)
				localStorage.setItem('access_token', response.data['access_token']);

			}).catch(e => setError('Неправильный код подтверждения'))


	}

	return (
		<div className=' LoginContainer LoginContainerVisible'>


			<button className='LoginContainer__CloseButton' onClick={() => clearLoginForm()} >╳</button>
			<div>На почту {store.user.email} отправлен код подтверждения </div>
			<form className='LoginContainer__Forms'>

				<input type='text' value={confirmCode} placeholder='' onChange={e => setConfirmCode(e.target.value)} />
				<div>{error}</div>

				<button>Отправить</button>



			</form >
		</div >

	)
}
