import React from 'react'
import { Context } from '../Context'
import { useContext } from 'react'
import { useEffect } from 'react'
import { useState } from 'react'
import api, { API_URL } from '../http'
import axios from 'axios'
import ResponseOption from './ResponseOption'
import { useTimer } from 'use-timer';
import Moment from "react-moment";
import { useLayoutEffect } from 'react'


export default function Test() {

	const [tests, setTests] = useState()
	const { setCurrentPage, store, setAuth } = useContext(Context)
	const [questionNumber, setQuestionNumber] = useState(0)
	const [apiTime, setApiTime] = useState()
	const [myTime, setMyTime] = useState()







	const { time, start, pause, reset, status, advanceTime } = useTimer({
		endTime: 900,



		initialTime: Number(myTime) - Number(apiTime),
		timerType: 'INREMENTAL',
		step: 1



	});

	advanceTime(1)
	useLayoutEffect(() =>
		(api.get(API_URL + "/Tests/user/test").then(x => {
			console.log(x)
			setTests(x.data)



			var s = new Date(x.data.testStartTime)
			setApiTime((
				(Number((s.getHours()) * 3600)) +
				(Number(s.getMinutes()) * 60) +
				(Number(s.getSeconds()))
			))
			var i = Date.now()

			var tpme = new Date(i)

			setMyTime(((Number(tpme.getHours()) * 3600) + (Number(tpme.getMinutes()) * 60) + (Number(tpme.getSeconds()))))

			console.log(((Number(tpme.getHours()) * 3600) + (Number(tpme.getMinutes()) * 60) + (Number(tpme.getSeconds()))))
			console.log((
				(Number((s.getHours()) * 3600)) +
				(Number(s.getMinutes()) * 60) +
				(Number(s.getSeconds()))
			))



		}
		)
		).then(reset).finally(start), [])

	useEffect(() => setCurrentPage('/pisa'), [])





	return (
		<div>

			<div className="HomeContainer__Header">
				<div className='HomeContainer__Header__Text TestsHeader'>

					<span>Тестирование</span>

				</div>
			</div>
			<div className='TestContainer'>
				<div className='TestContainer__Timer'>
					<span>Выберите один вариант ответа:</span>
					<span>Осталось времени:{Math.floor((900 - time) / 60) + ':' + ((900 - time) % 60)} </span>
				</div>
				<div className='TestContainer__QuestionContainer'>
					<span className='TestContainer__QuestionContainer__Number' >Вопрос №: 1.</span>
					<span className='TestContainer__QuestionContainer__Question'>{tests != null ? tests.test.question[questionNumber].question : 'Загрузака...'}</span>


				</div>

				<div className='TestContainer__Answers'>
					<div className='TestContainer__Answers__ResponseOptions'>

						{tests != null ? tests.test.question[questionNumber].responses.map(x => <ResponseOption response={x.response} />) : 'Загрузкаа...'}


					</div>
				</div>
				<div className='TestContainer__NavigateButtons'>
					<button onClick={() => setQuestionNumber(questionNumber - 1)}>
						Предыдущий вопрос

					</button>
					<button onClick={() => setQuestionNumber(questionNumber + 1)}>
						Следующий вопрос

					</button>
				</div>


			</div>
			<button className='SubmitButton'>Отправить</button>


		</div>
	)
}
