import { useEffect, useContext, useState } from "react"
import React from 'react'
import { Context } from "../Context"
import '../Source/Styles/Tests.css'
import Select from 'react-select';
import axios from "axios";
import api, { API_URL } from "../http";
import { Link } from "react-router-dom";

export default function Tests() {

  const [course, setCourse] = useState([
    { value: 1, label: 1 },
    { value: 2, label: 2 },
    { value: 3, label: 3 },

    { value: 4, label: 4 },
    { value: 5, label: 5 },
    { value: 6, label: 6 },



  ])
  const [department, setDepartment] = useState([])
  const [departmentId, setDepartmentId] = useState()
  const [courseId, setCourseId] = useState()



  useEffect(() =>
  (axios.get(API_URL + "/department/all").then(x => {

    const departmentOptions = x.data.map(pisa => new Object({ value: pisa.name, label: 'Кафедра ' + pisa.name }))
    setDepartment(departmentOptions)


  }
  )
  ), [])

  function Submit() {
    api.get('/Tests/get', data = { departmentId, courseId }).then(response => console.log(response))
  }


  const { setCurrentPage } = useContext(Context)
  useEffect(() => setCurrentPage('/tests'), [])
  return (
    <div>
      <div className="HomeContainer__Header">
        <div className='HomeContainer__Header__Text TestsHeader'>

          <span>Тестирование</span>

        </div>
      </div>
      <div className="TetstContainer" >
        <div className="TetstContainer__Selections" >
          <Select className='TetstContainer__CourseSelect' placeholder='Кафедра'

            onChange={e => setDepartmentId(value)}

            options={department}
          />

          <Select className='TetstContainer__CourseSelect' placeholder='Курс'

            onChange={e => setCourseId(value)}

            options={course}
          />
          <Link to='/pisa'><button className="TetstContainer__Submit" onClick={() => Submit()} > Пройти тестирование</button></Link>
        </div>



      </div>






    </div >
  )
}
